// chatbot-send-message.php - Handle chatbot messages and responses
header('Content-Type: application/json');

// Include CORS configuration
require_once '../config/cors.php';

// Include database connection
require_once '../config/db.php';

// Get JSON input
$input = json_decode(file_get_contents('php://input'), true);

if (!$input) {
    echo json_encode(['success' => false, 'message' => 'Invalid input']);
    exit;
}

$session_id = $input['session_id'] ?? '';
$user_name = $input['user_name'] ?? '';
$user_email = $input['user_email'] ?? '';
$user_phone = $input['user_phone'] ?? '';
$message = trim($input['message'] ?? '');

// Validate required fields
if (empty($session_id) || empty($message)) {
    echo json_encode(['success' => false, 'message' => 'Missing required fields']);
    exit;
}

try {
    // Generate AI response based on user message
    $bot_response = generateBotResponse($message);
    
    // Save user message
    $stmt = $pdo->prepare("
        INSERT INTO chatbot_conversations 
        (session_id, user_name, user_email, user_phone, message, response, message_type, created_at) 
        VALUES (?, ?, ?, ?, ?, '', 'user', NOW())
    ");
    $stmt->execute([$session_id, $user_name, $user_email, $user_phone, $message]);
    
    // Save bot response
    $stmt = $pdo->prepare("
        INSERT INTO chatbot_conversations 
        (session_id, user_name, user_email, user_phone, message, response, message_type, created_at) 
        VALUES (?, ?, ?, ?, '', ?, 'bot', NOW())
    ");
    $stmt->execute([$session_id, $user_name, $user_email, $user_phone, $bot_response]);
    
    echo json_encode([
        'success' => true,
        'response' => $bot_response
    ]);
    
} catch (PDOException $e) {
    echo json_encode(['success' => false, 'message' => 'Database error: ' . $e->getMessage()]);
}

